import React from "react";
import useForm from "./useForm";
import validate from "./LoginFormValidationRules";
import { Row, Container, Button, FormGroup, Input, Col, Card, CardBody } from "reactstrap";
import { logDOM } from "@testing-library/react";

const Form = () => {
  const { values, errors, handleChange, handleSubmit } = useForm(
    login,
    validate
  );

  function login() {
    console.log("No errors, submit callback called!");
  }

  function focusFunction() {
    // Focus = Changes the background color of input to yellow
    document.getElementsByClassName("form-control").style.background =
      "yellow";
  }


  return (
    <div className="users-wrap">
      <Container className="h-100">
        <Row className="h-100 justify-content-center align-items-center">
          <Col sm={12} md={6} lg={6} className="mx-auto">
            <img src="./logo.svg" />
          </Col>
          <Col sm={12} md={6} lg={6} className="mx-auto">
            <Card>
              <CardBody>
                <form onSubmit={handleSubmit} noValidate>
                  <div className="field">
                    <FormGroup>
                      <input
                        autoComplete="off"
                        className={`form-control input ${errors.email &&
                          "is-danger"}`}
                        type="email"
                        name="email"
                        placeholder="Email Address"
                        onChange={handleChange}
                        value={values.email || ""}
                        required
                      />
                      {errors.email && (
                        <p className="help is-danger">{errors.email}</p>
                      )}
                    </FormGroup>
                  </div>
                  <div className="field">
                    <FormGroup>
                      <input
                        className={`form-control input ${errors.password &&
                          "is-danger"}`}
                        type="password"
                        name="password"
                        placeholder="Enter Password"
                        onChange={handleChange}
                        value={values.password || ""}
                        required
                      />
                      {errors.password && (
                        <p className="help is-danger">{errors.password}</p>
                      )}
                    </FormGroup>
                  </div>
                  <Button color="primary" size="lg" block type="submit">
                    Login
                  </Button>
                </form>
              </CardBody>
            </Card>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default Form;
