import React from "react";
import { logout, isLogin } from "../utils";
import { Link } from "react-router-dom";
import {
  Row,
  Container,
  Button,
  FormGroup,
  Input,
  Col,
  Card,
  CardBody,
  CardTitle,
  CardSubtitle
} from "reactstrap";
function Home() {

    handleLogout = () => {
      logout();
      this.setState({
        isLogin: false
      });
    };
    
    return (
      <div>
        <h1>Home</h1>

        {this.state.isLogin ? (
          <button onClick={() => this.handleLogout()}>
            Click here to log out
          </button>
        ) : (
          <Link to="/signin">Go to sign in page</Link>
        )}
      </div>
    );
} export default Home
