import React from "react";
import useForm from "./useForm";
import validate from "./LoginFormValidationRules";
import { Row, Container, Button, FormGroup, Input, Col, Card, CardBody, CardTitle, CardSubtitle } from "reactstrap";
import { logDOM } from "@testing-library/react";
import InnerBgImg from "./../assets/images/login-bg.jpg";

const Form = () => {
  const { values, errors, handleChange, handleSubmit } = useForm(
    login,
    validate
  );

  function login() {
    console.log("No errors, submit callback called!");
  }

  function focusFunction() {
    // Focus = Changes the background color of input to yellow
    document.getElementsByClassName("form-control").style.background =
      "yellow";
  }


  return (
    <div
      className="users-wrap"
      style={{ backgroundImage: "url(" + InnerBgImg + ")" }}
    >
      <Container className="h-100">
        <Row className="h-100 justify-content-center align-items-center">
          <Col
            sm={12}
            md={6}
            lg={6}
            className="mx-auto d-flex align-items-center justify-content-center"
          >
            <img
              className="img-fluid align-self-center"
              src={require("./../assets/images/logo-nav.png")}
              alt="logo"
            />
          </Col>
          <Col sm={12} md={6} lg={6} className="mx-auto">
            <Card>
              <CardBody>
                <CardTitle>Logo</CardTitle>
                <CardSubtitle>
                  <h2>Sign In</h2>
                </CardSubtitle>
                <form onSubmit={handleSubmit} noValidate>
                  <div className="field">
                    <FormGroup>
                      <input
                        autoComplete="off"
                        className={`form-control form-control-lg input ${errors.email &&
                          "is-danger"}`}
                        type="email"
                        name="email"
                        onChange={handleChange}
                        value={values.email || ""}
                        required
                      />
                      <span className="floating-label">
                        Your email address*
                      </span>
                      {errors.email && (
                        <p className="help is-danger">{errors.email}</p>
                      )}
                    </FormGroup>
                  </div>
                  <div className="field">
                    <FormGroup>
                      <input
                        className={`form-control form-control-lg input ${errors.password &&
                          "is-danger"}`}
                        type="password"
                        name="password"
                        bssize="lg"
                        onChange={handleChange}
                        value={values.password || ""}
                        required
                      />
                      <span className="floating-label">Enter Password*</span>
                      {errors.password && (
                        <p className="help is-danger">{errors.password}</p>
                      )}
                    </FormGroup>
                  </div>
                  <Button
                    className="cta-book"
                    color="primary"
                    size="lg"
                    block
                    type="submit"
                  >
                    Login
                  </Button>
                </form>
                <a href="#">Don't have an account? Sign Up</a>
              </CardBody>
            </Card>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default Form;
